#include "Controller.h"Renderer::SGControllerFPS::SGControllerFPS(SGCamera * cam, int w, int h){	m_camera = cam;	m_width = w;	m_height = h;	m_mouseX = m_width / 2;	m_mouseY = m_height / 2;	Init();}Renderer::SGControllerFPS::~SGControllerFPS(){}void Renderer::SGControllerFPS::Move(){}void Renderer::SGControllerFPS::Init(){}void Renderer::SGControllerFPS::Update(){	Move();}void Renderer::SGControllerFPS::OnMouseMove(int x, int y){	glm::vec3 oldPosition = m_camera->GetPosition();	glm::vec3 newPosition = m_horizontal * Right() + m_vertical * Forward() + m_gravity * Up();	m_camera->SetPosition(newPosition * m_step);}void Renderer::SGControllerFPS::OnKeyboard(unsigned char key, int x, int y){}void Renderer::SGControllerFPS::OnKeyboardUp(unsigned char key, int x, int y){}